    fun pacificAtlantic(matrix: Array<IntArray>): List<List<Int>> {
        val result = listOf<List<Int>>(listOf())

        if (matrix.isEmpty()) return result;

        val m = matrix.size
        val n = matrix[0].size

        val atlantic = mutableListOf<MutableList<Boolean>>(mutableListOf())
        val pacific = mutableListOf<MutableList<Boolean>>(mutableListOf())

        // perform dfs from all edge cells (ocean-connected cells)
        for (i in 0..m) {
            dfs(matrix, pacific, i, 0)
            dfs(matrix, atlantic, i, n - 1)
        }
        for (i in 0..n) {
            dfs(matrix, pacific, 0, i)
            dfs(matrix, atlantic, m - 1, i)
        }

        return result;
    }

    fun dfs(matrix: Array<IntArray>, visited: MutableList<MutableList<Boolean>>, i: Int, j: Int) {

        if (visited[i][j]) return

        visited[i][j] = true;

        // if cell reachable from both the oceans, insert into final answer vector
//        if (atlantic[i][j] && pacific[i][j]) ans.push_back(vector<int> { i, j });
        // dfs from current cell only if height of next cell is greater
///*⬇️*/  if (i + 1 < m && matrix[i + 1][j] >= matrix[i][j]) dfs(matrix, visited, i + 1, j);
/*⬆️*/  if (i - 1 >= 0 && matrix[i - 1][j] >= matrix[i][j]) dfs(matrix, visited, i - 1, j);
///*➡️*/  if (j + 1 < n && matrix[i][j + 1] >= matrix[i][j]) dfs(matrix, visited, i, j + 1);
/*⬅️*/  if (j - 1 >= 0 && matrix[i][j - 1] >= matrix[i][j]) dfs(matrix, visited, i, j - 1);
    }
