    @Test
    fun testChallenge5() {
        Assert.assertEquals(3, threeSumMulti(intArrayOf(3,3,3,0), 6))
    }

    fun threeSumMulti(arr: IntArray, target: Int): Int {
        val mod = 1000000007
        var result = 0
        for (i in arr.indices) {
            val firstVal = arr[i]
            for (j in i + 1 until arr.size) {
                val secondVal = arr[j]
                for (k in j + 1 until arr.size) {
                    val thirdVal = arr[k]
                    val sum = firstVal + secondVal + thirdVal
                    if (sum == target) {
                        result = (result + 1) % mod
                    }
                }
            }
        }

        return result % mod
    }

}